-- First, let's clean up to have exactly 101 baseline profiles
-- Delete excess baseline profiles, keeping only 101
WITH baseline_to_keep AS (
  SELECT id 
  FROM user_profiles 
  WHERE is_custom = false 
  ORDER BY created_at 
  LIMIT 101
)
DELETE FROM user_profiles 
WHERE is_custom = false 
AND id NOT IN (SELECT id FROM baseline_to_keep);

